// 如果开通tpm的话 要提示预算使用预警值
Model.request('通用-方案-获取系统配置参数', {}, function (res) {
    console.log('success')
    console.log(res.kx_parameters)
    let msg = ''
    let prewarning = res.kx_parameters.prewarning
    let prewarningrate = res.kx_parameters.prewarningrate

    if (prewarningrate != '' && Number(prewarningrate) > 0) {
        if (Number(Page.getCtrl('预计费效比').value) * 100 > Number(prewarningrate)) {
            msg += '该方案预计费效比高于预警值！'
        }
    }

    if (isSupportTpm && prewarning != '' && Number(prewarning) > 0) {
        for (var i = 0, len = costRows.length; i < len; i++) {
            if (Number(costRows[i].availableamount) < Number(prewarning)) {
                msg += ' 可用余额低于预警值'
                break
            }
        }
    }

    if (msg) {
        Page.alert('info', msg, [{
            'title': '确定',
            'handler': function () {
                save()
            }
        }])
    } else {
        save()
    }
}, function (error) {
    console.error('查询预算预警值错误 直接保存')
    save()
})
